package com.virtualsoundnw.thing.web.rest;

import com.virtualsoundnw.thing.ThingApp;
import com.virtualsoundnw.thing.domain.Data;
import com.virtualsoundnw.thing.repository.DataRepository;
import com.virtualsoundnw.thing.service.DataService;
import com.virtualsoundnw.thing.service.dto.DataDTO;
import com.virtualsoundnw.thing.service.mapper.DataMapper;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import static org.hamcrest.Matchers.hasItem;
import org.mockito.MockitoAnnotations;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.http.MediaType;
import org.springframework.http.converter.json.MappingJackson2HttpMessageConverter;
import org.springframework.data.web.PageableHandlerMethodArgumentResolver;
import org.springframework.test.context.junit4.SpringRunner;
import org.springframework.test.util.ReflectionTestUtils;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.setup.MockMvcBuilders;
import org.springframework.transaction.annotation.Transactional;

import javax.annotation.PostConstruct;
import javax.inject.Inject;
import javax.persistence.EntityManager;
import java.time.LocalDate;
import java.time.ZoneId;
import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.*;

/**
 * Test class for the DataResource REST controller.
 *
 * @see DataResource
 */
@RunWith(SpringRunner.class)
@SpringBootTest(classes = ThingApp.class)
public class DataResourceIntTest {

    private static final LocalDate DEFAULT_DATE = LocalDate.ofEpochDay(0L);
    private static final LocalDate UPDATED_DATE = LocalDate.now(ZoneId.systemDefault());
    private static final String DEFAULT_JSON = "AA";
    private static final String UPDATED_JSON = "BB";
    private static final String DEFAULT_XML = "";
    private static final String UPDATED_XML = "";
    private static final String DEFAULT_TEXT = "AAAAA";
    private static final String UPDATED_TEXT = "BBBBB";

    @Inject
    private DataRepository dataRepository;

    @Inject
    private DataMapper dataMapper;

    @Inject
    private DataService dataService;

    @Inject
    private MappingJackson2HttpMessageConverter jacksonMessageConverter;

    @Inject
    private PageableHandlerMethodArgumentResolver pageableArgumentResolver;

    @Inject
    private EntityManager em;

    private MockMvc restDataMockMvc;

    private Data data;

    @PostConstruct
    public void setup() {
        MockitoAnnotations.initMocks(this);
        DataResource dataResource = new DataResource();
        ReflectionTestUtils.setField(dataResource, "dataService", dataService);
        this.restDataMockMvc = MockMvcBuilders.standaloneSetup(dataResource)
            .setCustomArgumentResolvers(pageableArgumentResolver)
            .setMessageConverters(jacksonMessageConverter).build();
    }

    /**
     * Create an entity for this test.
     *
     * This is a static method, as tests for other entities might also need it,
     * if they test an entity which requires the current entity.
     */
    public static Data createEntity(EntityManager em) {
        Data data = new Data();
        data = new Data()
                .date(DEFAULT_DATE)
                .json(DEFAULT_JSON)
                .xml(DEFAULT_XML)
                .text(DEFAULT_TEXT);
        return data;
    }

    @Before
    public void initTest() {
        data = createEntity(em);
    }

    @Test
    @Transactional
    public void createData() throws Exception {
        int databaseSizeBeforeCreate = dataRepository.findAll().size();

        // Create the Data
        DataDTO dataDTO = dataMapper.dataToDataDTO(data);

        restDataMockMvc.perform(post("/api/data")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(dataDTO)))
                .andExpect(status().isCreated());

        // Validate the Data in the database
        List<Data> data = dataRepository.findAll();
        assertThat(data).hasSize(databaseSizeBeforeCreate + 1);
        Data testData = data.get(data.size() - 1);
        assertThat(testData.getDate()).isEqualTo(DEFAULT_DATE);
        assertThat(testData.getJson()).isEqualTo(DEFAULT_JSON);
        assertThat(testData.getXml()).isEqualTo(DEFAULT_XML);
        assertThat(testData.getText()).isEqualTo(DEFAULT_TEXT);
    }

    @Test
    @Transactional
    public void checkDateIsRequired() throws Exception {
        int databaseSizeBeforeTest = dataRepository.findAll().size();
        // set the field null
        data.setDate(null);

        // Create the Data, which fails.
        DataDTO dataDTO = dataMapper.dataToDataDTO(data);

        restDataMockMvc.perform(post("/api/data")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(dataDTO)))
                .andExpect(status().isBadRequest());

        List<Data> data = dataRepository.findAll();
        assertThat(data).hasSize(databaseSizeBeforeTest);
    }

    @Test
    @Transactional
    public void getAllData() throws Exception {
        // Initialize the database
        dataRepository.saveAndFlush(data);

        // Get all the data
        restDataMockMvc.perform(get("/api/data?sort=id,desc"))
                .andExpect(status().isOk())
                .andExpect(content().contentType(MediaType.APPLICATION_JSON_UTF8_VALUE))
                .andExpect(jsonPath("$.[*].id").value(hasItem(data.getId().intValue())))
                .andExpect(jsonPath("$.[*].date").value(hasItem(DEFAULT_DATE.toString())))
                .andExpect(jsonPath("$.[*].json").value(hasItem(DEFAULT_JSON.toString())))
                .andExpect(jsonPath("$.[*].xml").value(hasItem(DEFAULT_XML.toString())))
                .andExpect(jsonPath("$.[*].text").value(hasItem(DEFAULT_TEXT.toString())));
    }

    @Test
    @Transactional
    public void getData() throws Exception {
        // Initialize the database
        dataRepository.saveAndFlush(data);

        // Get the data
        restDataMockMvc.perform(get("/api/data/{id}", data.getId()))
            .andExpect(status().isOk())
            .andExpect(content().contentType(MediaType.APPLICATION_JSON_UTF8_VALUE))
            .andExpect(jsonPath("$.id").value(data.getId().intValue()))
            .andExpect(jsonPath("$.date").value(DEFAULT_DATE.toString()))
            .andExpect(jsonPath("$.json").value(DEFAULT_JSON.toString()))
            .andExpect(jsonPath("$.xml").value(DEFAULT_XML.toString()))
            .andExpect(jsonPath("$.text").value(DEFAULT_TEXT.toString()));
    }

    @Test
    @Transactional
    public void getNonExistingData() throws Exception {
        // Get the data
        restDataMockMvc.perform(get("/api/data/{id}", Long.MAX_VALUE))
                .andExpect(status().isNotFound());
    }

    @Test
    @Transactional
    public void updateData() throws Exception {
        // Initialize the database
        dataRepository.saveAndFlush(data);
        int databaseSizeBeforeUpdate = dataRepository.findAll().size();

        // Update the data
        Data updatedData = dataRepository.findOne(data.getId());
        updatedData
                .date(UPDATED_DATE)
                .json(UPDATED_JSON)
                .xml(UPDATED_XML)
                .text(UPDATED_TEXT);
        DataDTO dataDTO = dataMapper.dataToDataDTO(updatedData);

        restDataMockMvc.perform(put("/api/data")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(dataDTO)))
                .andExpect(status().isOk());

        // Validate the Data in the database
        List<Data> data = dataRepository.findAll();
        assertThat(data).hasSize(databaseSizeBeforeUpdate);
        Data testData = data.get(data.size() - 1);
        assertThat(testData.getDate()).isEqualTo(UPDATED_DATE);
        assertThat(testData.getJson()).isEqualTo(UPDATED_JSON);
        assertThat(testData.getXml()).isEqualTo(UPDATED_XML);
        assertThat(testData.getText()).isEqualTo(UPDATED_TEXT);
    }

    @Test
    @Transactional
    public void deleteData() throws Exception {
        // Initialize the database
        dataRepository.saveAndFlush(data);
        int databaseSizeBeforeDelete = dataRepository.findAll().size();

        // Get the data
        restDataMockMvc.perform(delete("/api/data/{id}", data.getId())
                .accept(TestUtil.APPLICATION_JSON_UTF8))
                .andExpect(status().isOk());

        // Validate the database is empty
        List<Data> data = dataRepository.findAll();
        assertThat(data).hasSize(databaseSizeBeforeDelete - 1);
    }
}
